graph<template>

  <div id="chart" style="width: 100%; height:8rem"></div>
</template>
<script>
import * as  echarts from "echarts";
export default {
data() {
  return {
      graph:[],
  };
},
mounted() {  
  axios.get('https://echarts.apache.org/examples/data/asset/data/les-miserables.json')
      .then(response => {
        console.log(response.data+"XXXXXXXXXXXXXXXXXXXXXXXXXXX")
        this.graph = response.data;
      })
      .catch(error => {      console.log("DDDDDDDDDDDDDDDDDDDDDDD")
        console.error("Error fetching data:", error);
      });
  console.log("进来了")
  this.initChart()

},

methods: {
  fetchData() {

  },
  initChart() {
    //初始化echarts
    let myChart = echarts.init(document.getElementById('chart'))
    myChart.resize();  //自适应大小
    myChart.setOption(this.setOption());
    myChart.hideLoading();
  },
  //初始化echarts
  setOption() {
    let option = {
      tooltip: {},
    legend: [
      {
        data: graph.categories.map(function (a) {
          return a.name;
        })
      }
    ],
    series: [
      {
        name: 'Les Miserables',
        type: 'graph',
        layout: 'none',
        data: graph.nodes,
        links: graph.links,
        categories: graph.categories,
        roam: true,
        label: {
          show: true,
          position: 'right',
          formatter: '{b}'
        },
        labelLayout: {
          hideOverlap: true
        },
        scaleLimit: {
          min: 0.4,
          max: 2
        },
        lineStyle: {
          color: 'source',
          curveness: 0.3
        }
      }
    ]
    }
    return option;
  },
},
};
</script>

